import{_ as o,C as s,c as a,o as r,j as i,G as n,ag as l,a as d}from"./chunks/framework.Ds56KWXN.js";const x=JSON.parse('{"title":"System & Hosting Checks","description":"","frontmatter":{},"headers":[],"relativePath":"checks/system.md","filePath":"checks/system.md"}'),c={name:"checks/system.md"};function u(h,e,p,g,m,f){const t=s("CopyOrDownloadAsMarkdownButtons");return r(),a("div",null,[e[0]||(e[0]=i("div",{style:{display:"none"},hidden:"true","aria-hidden":"true"},"Are you an LLM? You can read better optimized documentation at /docs/checks/system.md for this page in Markdown format",-1)),e[1]||(e[1]=i("h1",{id:"system-hosting-checks",tabindex:"-1"},[d("System & Hosting Checks "),i("a",{class:"header-anchor",href:"#system-hosting-checks","aria-label":'Permalink to "System & Hosting Checks"'},"​")],-1)),n(t),e[2]||(e[2]=l('<p>System and hosting checks examine your PHP environment, server configuration, and hosting infrastructure. These form the foundation of a healthy Joomla site.</p><p><strong>Total checks in this category: 33</strong></p><h2 id="php-configuration-8-checks" tabindex="-1">PHP Configuration (8 checks) <a class="header-anchor" href="#php-configuration-8-checks" aria-label="Permalink to &quot;PHP Configuration (8 checks)&quot;">​</a></h2><h3 id="php-version" tabindex="-1">PHP Version <a class="header-anchor" href="#php-version" aria-label="Permalink to &quot;PHP Version&quot;">​</a></h3><p>Ensures PHP version meets Joomla 5 minimum requirements (8.1+).</p><ul><li><strong>Good</strong>: PHP 8.1.0 or higher</li><li><strong>Critical</strong>: Below PHP 8.1.0</li></ul><p><strong>Why it matters</strong>: Joomla 5 requires PHP 8.1+ for security, performance, and compatibility.</p><h3 id="memory-limit" tabindex="-1">Memory Limit <a class="header-anchor" href="#memory-limit" aria-label="Permalink to &quot;Memory Limit&quot;">​</a></h3><p>Checks PHP memory limit is sufficient.</p><ul><li><strong>Good</strong>: 256MB or higher</li><li><strong>Warning</strong>: Below 256MB</li><li><strong>Critical</strong>: Below 128MB</li></ul><p><strong>Why it matters</strong>: Insufficient memory causes errors, failed updates, and backup failures.</p><h3 id="max-execution-time" tabindex="-1">Max Execution Time <a class="header-anchor" href="#max-execution-time" aria-label="Permalink to &quot;Max Execution Time&quot;">​</a></h3><p>Verifies scripts have enough time to complete.</p><ul><li><strong>Good</strong>: 60 seconds or more</li><li><strong>Warning</strong>: Below 60 seconds</li><li><strong>Critical</strong>: Below 30 seconds</li></ul><p><strong>Why it matters</strong>: Long operations (backups, updates) need adequate execution time.</p><h3 id="max-input-time" tabindex="-1">Max Input Time <a class="header-anchor" href="#max-input-time" aria-label="Permalink to &quot;Max Input Time&quot;">​</a></h3><p>Checks time allowed for POST/upload processing.</p><ul><li><strong>Good</strong>: 60 seconds or more</li><li><strong>Warning</strong>: Below 60 seconds</li></ul><p><strong>Why it matters</strong>: Large file uploads require sufficient input processing time.</p><h3 id="max-input-variables" tabindex="-1">Max Input Variables <a class="header-anchor" href="#max-input-variables" aria-label="Permalink to &quot;Max Input Variables&quot;">​</a></h3><p>Ensures enough POST variables can be processed.</p><ul><li><strong>Good</strong>: 2000 or more</li><li><strong>Warning</strong>: Below 2000</li><li><strong>Critical</strong>: Below 1000</li></ul><p><strong>Why it matters</strong>: Complex forms and extensions need adequate variable limits.</p><h3 id="post-max-size" tabindex="-1">Post Max Size <a class="header-anchor" href="#post-max-size" aria-label="Permalink to &quot;Post Max Size&quot;">​</a></h3><p>Checks maximum POST request size.</p><ul><li><strong>Good</strong>: 32MB or higher</li><li><strong>Warning</strong>: Below 32MB</li><li><strong>Critical</strong>: Below 8MB</li></ul><p><strong>Why it matters</strong>: Affects maximum file upload sizes and form submissions.</p><h3 id="upload-max-filesize" tabindex="-1">Upload Max Filesize <a class="header-anchor" href="#upload-max-filesize" aria-label="Permalink to &quot;Upload Max Filesize&quot;">​</a></h3><p>Verifies maximum file upload size.</p><ul><li><strong>Good</strong>: 32MB or higher</li><li><strong>Warning</strong>: Below 32MB</li><li><strong>Critical</strong>: Below 8MB</li></ul><p><strong>Why it matters</strong>: Users need to upload images, documents, and media files.</p><h3 id="output-buffering" tabindex="-1">Output Buffering <a class="header-anchor" href="#output-buffering" aria-label="Permalink to &quot;Output Buffering&quot;">​</a></h3><p>Checks if output buffering is enabled.</p><ul><li><strong>Good</strong>: Enabled</li><li><strong>Warning</strong>: Disabled</li></ul><p><strong>Why it matters</strong>: Prevents &quot;headers already sent&quot; errors.</p><h2 id="required-php-extensions-11-checks" tabindex="-1">Required PHP Extensions (11 checks) <a class="header-anchor" href="#required-php-extensions-11-checks" aria-label="Permalink to &quot;Required PHP Extensions (11 checks)&quot;">​</a></h2><h3 id="json-extension" tabindex="-1">JSON Extension <a class="header-anchor" href="#json-extension" aria-label="Permalink to &quot;JSON Extension&quot;">​</a></h3><p>Verifies JSON support is available.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for Joomla core functionality and modern web APIs.</p><h3 id="simplexml-extension" tabindex="-1">SimpleXML Extension <a class="header-anchor" href="#simplexml-extension" aria-label="Permalink to &quot;SimpleXML Extension&quot;">​</a></h3><p>Checks XML parsing support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for Joomla XML manifests and configuration files.</p><h3 id="dom-extension" tabindex="-1">DOM Extension <a class="header-anchor" href="#dom-extension" aria-label="Permalink to &quot;DOM Extension&quot;">​</a></h3><p>Verifies DOM manipulation support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Essential for HTML/XML processing in Joomla core.</p><h3 id="pdo-mysql-extension" tabindex="-1">PDO MySQL Extension <a class="header-anchor" href="#pdo-mysql-extension" aria-label="Permalink to &quot;PDO MySQL Extension&quot;">​</a></h3><p>Checks database connectivity support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for all database operations in Joomla 5.</p><h3 id="gd-or-imagick" tabindex="-1">GD or Imagick <a class="header-anchor" href="#gd-or-imagick" aria-label="Permalink to &quot;GD or Imagick&quot;">​</a></h3><p>Verifies image processing capability.</p><ul><li><strong>Good</strong>: GD or Imagick installed</li><li><strong>Warning</strong>: Neither installed</li></ul><p><strong>Why it matters</strong>: Needed for thumbnail generation and image manipulation.</p><h3 id="zip-extension" tabindex="-1">Zip Extension <a class="header-anchor" href="#zip-extension" aria-label="Permalink to &quot;Zip Extension&quot;">​</a></h3><p>Checks archive handling support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for extension installation and updates.</p><h3 id="mbstring-extension" tabindex="-1">Mbstring Extension <a class="header-anchor" href="#mbstring-extension" aria-label="Permalink to &quot;Mbstring Extension&quot;">​</a></h3><p>Verifies multibyte string support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Warning</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Essential for proper handling of international characters.</p><h3 id="intl-extension" tabindex="-1">Intl Extension <a class="header-anchor" href="#intl-extension" aria-label="Permalink to &quot;Intl Extension&quot;">​</a></h3><p>Checks internationalization support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Warning</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for proper date/time/currency formatting across languages.</p><h3 id="curl-extension" tabindex="-1">cURL Extension <a class="header-anchor" href="#curl-extension" aria-label="Permalink to &quot;cURL Extension&quot;">​</a></h3><p>Verifies HTTP request capability.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Warning</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Used for remote API calls and update checks.</p><h3 id="openssl-extension" tabindex="-1">OpenSSL Extension <a class="header-anchor" href="#openssl-extension" aria-label="Permalink to &quot;OpenSSL Extension&quot;">​</a></h3><p>Checks SSL/TLS support.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Critical</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Required for HTTPS connections and secure communications.</p><h3 id="fileinfo-extension" tabindex="-1">Fileinfo Extension <a class="header-anchor" href="#fileinfo-extension" aria-label="Permalink to &quot;Fileinfo Extension&quot;">​</a></h3><p>Verifies MIME type detection.</p><ul><li><strong>Good</strong>: Installed and enabled</li><li><strong>Warning</strong>: Missing or disabled</li></ul><p><strong>Why it matters</strong>: Helps prevent malicious file uploads by detecting true file types.</p><h2 id="server-environment-6-checks" tabindex="-1">Server Environment (6 checks) <a class="header-anchor" href="#server-environment-6-checks" aria-label="Permalink to &quot;Server Environment (6 checks)&quot;">​</a></h2><h3 id="disk-space-available" tabindex="-1">Disk Space Available <a class="header-anchor" href="#disk-space-available" aria-label="Permalink to &quot;Disk Space Available&quot;">​</a></h3><p>Monitors available disk space.</p><ul><li><strong>Good</strong>: More than 20% free or 1GB+</li><li><strong>Warning</strong>: 10-20% free</li><li><strong>Critical</strong>: Less than 10% free or under 500MB</li></ul><p><strong>Why it matters</strong>: Insufficient disk space prevents backups, uploads, and log writing.</p><h3 id="temp-directory-writable" tabindex="-1">Temp Directory Writable <a class="header-anchor" href="#temp-directory-writable" aria-label="Permalink to &quot;Temp Directory Writable&quot;">​</a></h3><p>Checks if temporary directory is writable.</p><ul><li><strong>Good</strong>: Writable</li><li><strong>Critical</strong>: Not writable</li></ul><p><strong>Why it matters</strong>: Required for file uploads, session storage, and temp operations.</p><h3 id="server-time-sync" tabindex="-1">Server Time Sync <a class="header-anchor" href="#server-time-sync" aria-label="Permalink to &quot;Server Time Sync&quot;">​</a></h3><p>Verifies server clock accuracy.</p><ul><li><strong>Good</strong>: Within 5 minutes of actual time</li><li><strong>Warning</strong>: 5-30 minutes off</li><li><strong>Critical</strong>: More than 30 minutes off</li></ul><p><strong>Why it matters</strong>: Accurate time is critical for SSL certificates, sessions, and scheduling.</p><h3 id="php-sapi-type" tabindex="-1">PHP SAPI Type <a class="header-anchor" href="#php-sapi-type" aria-label="Permalink to &quot;PHP SAPI Type&quot;">​</a></h3><p>Checks PHP execution interface.</p><ul><li><strong>Good</strong>: FPM, CGI/FastCGI, or LiteSpeed</li><li><strong>Warning</strong>: Apache module or CLI</li></ul><p><strong>Why it matters</strong>: Modern SAPI types offer better performance and security.</p><h3 id="php-end-of-life-status" tabindex="-1">PHP End-of-Life Status <a class="header-anchor" href="#php-end-of-life-status" aria-label="Permalink to &quot;PHP End-of-Life Status&quot;">​</a></h3><p>Checks if your PHP version is approaching or past its end-of-life date.</p><ul><li><strong>Good</strong>: PHP under active support with 90+ days remaining</li><li><strong>Warning</strong>: PHP in security-only support or nearing EOL</li><li><strong>Critical</strong>: PHP past end-of-life (no security patches)</li></ul><p><strong>Why it matters</strong>: PHP versions have defined support lifecycles:</p><ul><li><strong>Active support</strong>: Bug fixes and security patches</li><li><strong>Security support</strong>: Security patches only</li><li><strong>End of life</strong>: No patches at all</li></ul><p>Running an EOL PHP version means your server has known, unpatched security vulnerabilities.</p><p><strong>How to fix</strong>: Contact your hosting provider to upgrade PHP to a supported version.</p><h3 id="opcache-status" tabindex="-1">OPcache Status <a class="header-anchor" href="#opcache-status" aria-label="Permalink to &quot;OPcache Status&quot;">​</a></h3><p>Verifies PHP opcache is enabled.</p><ul><li><strong>Good</strong>: Enabled and active</li><li><strong>Warning</strong>: Disabled</li></ul><p><strong>Why it matters</strong>: Opcache dramatically improves PHP performance.</p><h3 id="realpath-cache-size" tabindex="-1">Realpath Cache Size <a class="header-anchor" href="#realpath-cache-size" aria-label="Permalink to &quot;Realpath Cache Size&quot;">​</a></h3><p>Checks realpath cache configuration.</p><ul><li><strong>Good</strong>: 4MB or higher</li><li><strong>Warning</strong>: Below 4MB</li></ul><p><strong>Why it matters</strong>: Larger cache reduces filesystem overhead.</p><h2 id="log-scheduler-3-checks" tabindex="-1">Log &amp; Scheduler (3 checks) <a class="header-anchor" href="#log-scheduler-3-checks" aria-label="Permalink to &quot;Log &amp; Scheduler (3 checks)&quot;">​</a></h2><h3 id="log-file-size" tabindex="-1">Log File Size <a class="header-anchor" href="#log-file-size" aria-label="Permalink to &quot;Log File Size&quot;">​</a></h3><p>Monitors the total size of files in Joomla&#39;s log directory.</p><ul><li><strong>Good</strong>: Log directory ≤100MB</li><li><strong>Warning</strong>: Log directory 100-500MB</li><li><strong>Critical</strong>: Log directory &gt;500MB</li></ul><p><strong>Why it matters</strong>: Log files grow continuously and can quickly consume disk space:</p><ul><li>Runaway errors can generate gigabytes of logs in hours</li><li>Large log files slow down analysis and debugging</li><li>Full disks cause site failures, database corruption, and data loss</li><li>Old logs may contain sensitive information</li></ul><p><strong>How to manage</strong>:</p><ol><li>Configure Joomla&#39;s Task Scheduler for log rotation</li><li>Regularly review and archive old logs</li><li>Investigate recurring errors that fill logs</li><li>Consider external log management solutions</li></ol><h3 id="overdue-scheduled-tasks" tabindex="-1">Overdue Scheduled Tasks <a class="header-anchor" href="#overdue-scheduled-tasks" aria-label="Permalink to &quot;Overdue Scheduled Tasks&quot;">​</a></h3><p>Identifies tasks that haven&#39;t run on schedule.</p><ul><li><strong>Good</strong>: No overdue tasks</li><li><strong>Warning</strong>: 1-5 overdue tasks</li><li><strong>Critical</strong>: More than 5 overdue tasks</li></ul><p><strong>Why it matters</strong>: Overdue tasks indicate scheduler problems or server issues.</p><h3 id="failed-scheduled-tasks" tabindex="-1">Failed Scheduled Tasks <a class="header-anchor" href="#failed-scheduled-tasks" aria-label="Permalink to &quot;Failed Scheduled Tasks&quot;">​</a></h3><p>Detects tasks that have failed.</p><ul><li><strong>Good</strong>: No failed tasks</li><li><strong>Warning</strong>: 1-3 recent failures</li><li><strong>Critical</strong>: More than 3 failures or repeated failures</li></ul><p><strong>Why it matters</strong>: Failed tasks may indicate configuration problems or errors.</p><h2 id="common-issues-solutions" tabindex="-1">Common Issues &amp; Solutions <a class="header-anchor" href="#common-issues-solutions" aria-label="Permalink to &quot;Common Issues &amp; Solutions&quot;">​</a></h2><h3 id="low-memory-limit" tabindex="-1">Low Memory Limit <a class="header-anchor" href="#low-memory-limit" aria-label="Permalink to &quot;Low Memory Limit&quot;">​</a></h3><p><strong>Symptoms</strong>: Extension installation fails, white screens, timeouts</p><p><strong>Solutions</strong>:</p><ol><li>Contact hosting provider to increase PHP memory limit</li><li>Add to <code>.htaccess</code>: <code>php_value memory_limit 256M</code></li><li>Add to <code>php.ini</code>: <code>memory_limit = 256M</code></li><li>Upgrade hosting plan if current plan restricts PHP settings</li></ol><h3 id="missing-php-extensions" tabindex="-1">Missing PHP Extensions <a class="header-anchor" href="#missing-php-extensions" aria-label="Permalink to &quot;Missing PHP Extensions&quot;">​</a></h3><p><strong>Symptoms</strong>: Errors during installation, missing features</p><p><strong>Solutions</strong>:</p><ol><li>Contact hosting provider to enable required extensions</li><li>Use hosting control panel (if available) to enable extensions</li><li>For VPS/dedicated: Install via package manager <ul><li>Ubuntu/Debian: <code>apt-get install php8.1-{extension}</code></li><li>CentOS/RHEL: <code>yum install php81-{extension}</code></li></ul></li></ol><h3 id="disk-space-issues" tabindex="-1">Disk Space Issues <a class="header-anchor" href="#disk-space-issues" aria-label="Permalink to &quot;Disk Space Issues&quot;">​</a></h3><p><strong>Symptoms</strong>: Failed backups, upload errors, slow performance</p><p><strong>Solutions</strong>:</p><ol><li>Clean up old backups and log files</li><li>Remove unused media files</li><li>Empty trash folders</li><li>Upgrade hosting plan for more storage</li><li>Use external storage for backups</li></ol><h3 id="outdated-php-version" tabindex="-1">Outdated PHP Version <a class="header-anchor" href="#outdated-php-version" aria-label="Permalink to &quot;Outdated PHP Version&quot;">​</a></h3><p><strong>Symptoms</strong>: Compatibility warnings, security alerts</p><p><strong>Solutions</strong>:</p><ol><li>Contact hosting provider to upgrade PHP version</li><li>Use hosting control panel to switch PHP version</li><li>Test on staging site first before production upgrade</li><li>Review extension compatibility before upgrading</li></ol><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="./database">Database Checks</a> - Monitor database health</li><li><a href="./security">Security Checks</a> - Evaluate security settings</li><li><a href="./performance">Performance Checks</a> - Optimize site speed</li></ul>',147))])}const k=o(c,[["render",u]]);export{x as __pageData,k as default};
